import Head from 'next/head';

class HeadElement extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      currentUrl: '',
      title: ''
    };
    this.setTitle = this.setTitle.bind(this);
    this.resetZoom = this.resetZoom.bind(this);
  }
    setTitle () {
      let title;
      let cpn = window.location.pathname;
      if (cpn == '/') { title = 'MYNA Home'; }
      if (cpn == '/lookbook') { title = 'MYNA Lookbook'; }
      if (cpn == '/our-story') { title = 'MYNA Our Story'; }
      if (cpn == '/product-page') { title = 'Product template'; }
      this.setState({ currentUrl: cpn });
      this.setState({ title: title });
    }
    resetZoom () {
      var scale = 'scale(1)';
      document.body.style.webkitTransform =  scale;    // Chrome, Opera, Safari
      document.body.style.msTransform =   scale;       // IE 9
      document.body.style.transform = scale;     // General
      console.log("reset");
    }
    componentDidMount() {
      setTimeout(this.setTitle, 250);
      setTimeout(this.resetZoom, 500);
    }

    render() {
        return <Head>
        <title>{ this.state.title }</title>
        <meta charset="utf-8" />
        <meta name="viewport" content="width=device-width, initial-scale=1.0" />
        <script src="https://unpkg.com/react/umd/react.production.min.js" crossorigin></script>
        <script src="https://unpkg.com/react-dom/umd/react-dom.production.min.js" crossorigin></script>
        <script src="https://unpkg.com/react-bootstrap@next/dist/react-bootstrap.min.js" crossorigin></script>
        <link rel="stylesheet" href="https://maxcdn.bootstrapcdn.com/bootstrap/4.5.0/css/bootstrap.min.css" integrity="sha384-9aIt2nRpC12Uk9gS9baDl411NQApFmC26EwAOH8WgZl5MYYxFfc+NcPb1dKGj7Sk" crossorigin="anonymous" />
        </Head>
    }
}

export default HeadElement
