import React, { useState } from 'react';
import ReactDOM from 'react-dom';
//import { useRouter } from 'next/router';
import {API_SERVER as API_SERVER} from '../src/constants';
import Cookies from 'universal-cookie';
//import UserMenu from '../components/UserMenu';
import Header from '../components/Header';
import Nav from '../components/Nav';
import Footer from '../components/Footer';
import Container from 'react-bootstrap/Container';
import Button from 'react-bootstrap/Button';
//import 'bootstrap/dist/css/bootstrap.min.css';
import "../src/styles.css";
const cookies = new Cookies();
const hash = cookies.get('hash');

export default class Index extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      nextPage: '',
      photo1: 'https://myca.hende.org/userphotos/photo1.jpg',
      photo2: 'https://myca.hende.org/userphotos/photo2.jpg',
      photo3: 'https://myca.hende.org/userphotos/photo3.jpg',
      photo4: 'https://myca.hende.org/userphotos/photo4.jpg'
    };

    this.connectionAlive = this.connectionAlive.bind(this);

  }

  connectionAlive() {

  }

  componentDidUpdate() {
  }
  componentDidMount() {
    console.log (window.location.pathname);
  }
  render() {
		return (
			<Container fluid>
      <Header />
        <Nav />
        <div className="spacer50px" />
        <div className="row">
          <div className="col-md-1" />
          <div className="col-md-10">
            <div className="row">
              <div className="col-md-6 ce">
                <div className="row">
                  <div className="col-md-12">
                    <img src={ this.state.photo1 } width="450" height="450" />
                  </div>
                </div>
                <div className="spacer50px" />
                <div className="row">
                  <div className="col-md-4 ce">
                    <img src={ this.state.photo2 } width="150" height="150" />
                  </div>
                  <div className="col-md-4 ce">
                    <img src={ this.state.photo3 } width="150" height="150" />
                  </div>
                  <div className="col-md-4 ce">
                    <img src={ this.state.photo4 } width="150" height="150" />
                  </div>
                </div>
              </div>
              <div className="col-md-6 ce">
                <div className="row">
                  <div className="col-md-12">
                    <h2 className="capitalLetters">Tuli Vencel Woven Dress | Peach Glow | €100</h2>
                  </div>
                </div>
                <div className="spacer20px" />
                <div className="row">
                  <div className="col-md-3">
                    <a href="#" className="capitalLetters smallFont blackFont" id="productDesc">Description</a>
                  </div>
                  <div className="col-md-3">
                    <a href="#" className="capitalLetters smallFont blackFont" id="productComp">Composition & Care</a>
                  </div>
                  <div className="col-md-3">
                    <a href="#" className="capitalLetters smallFont blackFont" id="productSize">Size</a>
                  </div>
                  <div className="col-md-3">
                    <a href="#" className="capitalLetters smallFont blackFont" id="productDeli">Delivery</a>
                  </div>
                </div>
                <div className="spacer20px" />
                <div className="row">
                  <div className="col-md-12">
                    <div>{ this.state.productInfo }</div>
                  </div>
                </div>
              </div>
            </div>
          </div>
          <div className="col-md-1" />
        </div>

        <Footer />
      </Container>
		);
	}
}
