import React, { useState } from 'react';
import ReactDOM from 'react-dom';
//import { useRouter } from 'next/router';
import {API_SERVER as API_SERVER} from '../src/constants';
import Cookies from 'universal-cookie';
//import UserMenu from '../components/UserMenu';
import Header from '../components/Header';
import Nav from '../components/Nav';
import Footer from '../components/Footer';
import Ping from '../components/Ping';
import Container from 'react-bootstrap/Container';
import Button from 'react-bootstrap/Button';
//import 'bootstrap/dist/css/bootstrap.min.css';
import "../src/styles.css";
const cookies = new Cookies();
const session = cookies.get('session');

export default class Index extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      myEmail: '',
      loggedIn: 'no',
      activeCoupon: '',
      products: [],
      productsImg: [],
      inCart: '0',
      productRender: []
    };

    this.amILoggedIn = this.amILoggedIn.bind(this);
    this.getProductsInCart = this.getProductsInCart.bind(this);
    this.getImages = this.getImages.bind(this);
    this.getInCart = this.getInCart.bind(this);
    this.createProductRender = this.createProductRender.bind(this);
    this.addImages = this.addImages.bind(this);

  }

  amILoggedIn () {
    fetch(API_SERVER + 'listen.php?part=amiloggedin&sessiontoken=' + session)
    .then(response => response.json())
		.then(output => {
      let data = output;
      let tmp = data['email'];
      if (tmp != "nodata") {
        this.setState({ myEmail: tmp, loggedIn: 'yes' });
      } else {
        window.location.href = "/my-account";
      }
    })
    .catch(error => console.log(error.message));
  }
  getProductsInCart () {
    fetch(API_SERVER + 'listen.php?part=getproductsincart&sessiontoken=' + session)
    .then(response => response.json())
		.then(output => {
      let data = output;
      let tmp = data['products'];
      this.setState({ products: tmp });
      //console.log (this.state.products);
    })
    .catch(error => console.log(error.message));
  }
  getImages (e) {
    let idName = e;
    fetch(API_SERVER + 'listen.php?part=getproductdata&productname=' + idName)
    .then(response => response.json())
		.then(output => {
      let data = output;
      let tmp = data['productdetails'];
      let pic = tmp['pic1'];
      this.setState(previousState => ({
        productsImg: [...previousState.productsImg, pic]
      }));
      console.log (pic);
    })
    .catch(error => console.log(error.message));
  }
  getInCart () {
    fetch(API_SERVER + 'listen.php?part=getproductsnumberincart&sessiontoken=' + session)
    .then(response => response.json())
		.then(output => {
      let data = output;
      let tmp = data['nr'];
      this.setState({ inCart: tmp });
    })
    .catch(error => console.log(error.message));
  }
  createProductRender () {
    let nr = this.state.inCart;
    let prod = this.state.products;
    let hlp;
    let imgsrc;
    let imgtmp;
    let i;
    let tmp;

    //console.log (this.state.productsImg);

    /*for (i=0; i<nr; i++) {
      console.log ('okokok');
      console.log (this.state.productsImg);
      imgsrc = API_SERVER + 'productphotos/' + imgtmp[i];
      console.log (imgtmp);
      tmp = <div className="row"><div className="col-md-4"><img className="dyn" src={imgsrc} /></div></div>
    }
    this.setState({ productRender: tmp });*/
  }
  addImages () {
    let nr = this.state.inCart;
    let prod = this.state.products;
    let hlp;
    let i;
    for (i=0; i<nr; i++) {
      hlp = prod[i]['idname'];
      this.getImages (hlp);
      console.log (hlp);
    }
  }

  componentDidUpdate() {
    this.getInCart();
  }
  componentDidMount() {
    this.amILoggedIn();
    this.getProductsInCart();
    this.getInCart();
    setTimeout(this.addImages, 250);
    setTimeout(this.createProductRender, 500);
  }
  render() {
		return (
			<Container fluid>
      <Header />
        <Nav />
        <Ping />
        <div className="spacer50px" />
        <div className="row">
          <div className="col-md-12 ce capitalLetters">
            <h2><strong>Your Loved Pieces</strong></h2>
          </div>
        </div>
        <div className="spacer25px" />
        <div className="row">
          <div className="col-md-2" />
          <div className="col-md-8">
            Cart / {this.state.inCart} items
            <hr />
            {this.state.productRender}
          </div>
          <div className="col-md-2" />
        </div>


        <Footer />
      </Container>
		);
	}
}
