import React, { useState } from 'react';
import ReactDOM from 'react-dom';
import Link from 'next/link';
import { useRouter } from "next/router";
import {API_SERVER as API_SERVER} from '../src/constants';
import Cookies from 'universal-cookie';
//import UserMenu from '../components/UserMenu';
import Header from '../components/Header';
import Nav from '../components/Nav';
import Footer from '../components/Footer';
import Container from 'react-bootstrap/Container';
import Button from 'react-bootstrap/Button';
import Modal from 'react-awesome-modal';
//import 'bootstrap/dist/css/bootstrap.min.css';
import "../src/styles.css";
const cookies = new Cookies();
const hash = cookies.get('hash');
const session = cookies.get('session');

export default class Index extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      idName: 'nolia',
      currency: 'â‚¬',
      availability: '',
      productName: '',
      productColor: '',
      productPrice: '',
      description: '',
      compCare: '',
      session: '',
      fade: 'fadeIn',
      visible: false,
      nextPage: '',
      photo1: 'https://myca.hende.org/userphotos/photo1.jpg',
      photo2: 'https://myca.hende.org/userphotos/photo2.jpg',
      photo3: 'https://myca.hende.org/userphotos/photo3.jpg',
      photo4: 'https://myca.hende.org/userphotos/photo4.jpg',
      photo5: '',
      photo6: '',
      photo7: '',
      photo8: '',
      photo9: '',
      productInfo: <div><p>Flowing woven tencel midi dress with pockets.<br />The model is 170cm and is wearing size S.</p><p>This dress was handmade in Poland.</p></div>
    };

    this.connectionAlive = this.connectionAlive.bind(this);
    this.loadData = this.loadData.bind(this);
    this.productInfoHandling = this.productInfoHandling.bind(this);
    this.productPhotoHandling = this.productPhotoHandling.bind(this);
    this.fadeOut = this.fadeOut.bind(this);
    this.fadeIn = this.fadeIn.bind(this);
    this.openModal = this.openModal.bind(this);
		this.closeModal = this.closeModal.bind(this);

  }

  connectionAlive() {
    if (hash != 'undefined') { cookies.set("hash", hash, { maxAge: 3600 }); }
    if (session != 'undefined') { cookies.set("session", session, { maxAge: 3600 }); }
    let searchParams = new URLSearchParams(window.location.search);
    if (searchParams.has('session')) {
      let sessionTmp = searchParams.get('session');
      let tmp = "?session=" + sessionTmp;
      this.setState({ session: tmp });
    }
  }
  loadData () {
    fetch(API_SERVER + 'listen.php?part=getproductdata&productname=nolia')
    .then(response => response.json())
		.then(output => {
      let data = output;
      let id = data['productdetails']['id'];
      let availability = data['productdetails']['availability'];
      let productName = data['productdetails']['productname'];
      let productColor = data['productdetails']['productcolor'];
      let productPrice = data['productdetails']['productprice'];
      let description = data['productdetails']['desclong'];
      let compCare = data['productdetails']['compcare'];
      let photo1 = data['productdetails']['pic1'];
      let photo2 = data['productdetails']['pic2'];
      let photo3 = data['productdetails']['pic3'];
      let photo4 = data['productdetails']['pic4'];
      let photo5 = data['productdetails']['pic5'];
      let photo6 = data['productdetails']['pic6'];
      let photo7 = data['productdetails']['pic7'];
      let photo8 = data['productdetails']['pic8'];
      let photo9 = data['productdetails']['pic9'];
      this.setState({ availability: availability,
                      productName: productName,
                      productColor: productColor,
                      productPrice: productPrice,
                      description: description,
                      compCare: compCare,
                      productInfo: description,
                      availability: 0,
                      photo1: API_SERVER + 'productphotos/' + photo1,
                      photo2: API_SERVER + 'productphotos/' + photo2,
                      photo3: API_SERVER + 'productphotos/' + photo3,
                      photo4: API_SERVER + 'productphotos/' + photo4,
                      photo5: API_SERVER + 'productphotos/' + photo5,
                      photo6: API_SERVER + 'productphotos/' + photo6,
                      photo7: API_SERVER + 'productphotos/' + photo7,
                      photo8: API_SERVER + 'productphotos/' + photo8,
                      photo9: API_SERVER + 'productphotos/' + photo9
                    });
    })
    .catch(error => console.log(error.message));
    console.log (this.state.productName);
  }
  productInfoHandling(e) {
    let desc = this.state.description;
    let compCare = this.state.compCare;
    let prName = this.state.productInfo;
    let currentId = e.currentTarget.id;
    if (currentId == 'productDesc') {
      this.setState({
  			productInfo: desc
  		});
    }
    if (currentId == 'productComp') {
      this.setState({
  			productInfo: compCare
  		});
    }
    if (currentId == 'productSize') {
      this.setState({
  			productInfo: <div><div className="row"><div className="col-md-3">XS<br />Waist: 66cm<br />Bust: 84cm<br />Hip: 90cm</div><div className="col-md-3">S<br />Waist: 69cm<br />Bust: 89cm<br />Hip: 95cm</div><div className="col-md-3">M<br />Waist: 74cm<br />Bust: 94cm<br />Hip: 100cm</div><div className="col-md-3">L<br />Waist: 82cm<br />Bust: 10cm<br />Hip: 108cm</div></div></div>
  		});
    }
    if (currentId == 'productDeli') {
      this.setState({
  			productInfo: <div><p>Receive your favourite items(s) in 3 to 5 days with priority shipping method.<br />For countries outside of Europe shipping may take up to 10 days.<br />All items are shipped within 3 days from placing your order, with the exception of pre-ordered and custom made pieces. Pre-order pieces are made within 1 to 2 weeks. Email us at connect@mynalabel.com. You have 14 days to change your mind. Custom orders are non refundable.</p></div>
  		});
    }
  }
  productPhotoHandling(e) {
    let currentId = e.currentTarget.id;
    let tmp1 = this.state.photo1;
    let tmp2;
    if (currentId == 'photo1') { this.openModal(); } else { this.fadeOut (); setTimeout(this.fadeIn, 100); }
    if (currentId == 'photo2') {
      tmp2 = this.state.photo2;
      this.setState({ photo1: tmp2, photo2: tmp1 });
    }
    if (currentId == 'photo3') {
      tmp2 = this.state.photo3;
      this.setState({ photo1: tmp2, photo3: tmp1 });
    }
    if (currentId == 'photo4') {
      tmp2 = this.state.photo4;
      this.setState({ photo1: tmp2, photo4: tmp1 });
    }
  }
  fadeOut () {
    this.setState({ fade: 'fadeNone' });
  }
  fadeIn () {
    this.setState({ fade: 'fadeIn' });
  }
  openModal() {
		this.setState({
			visible : true
		});
	}
	closeModal() {
		this.setState({
			visible : false
		});
	}

  componentDidUpdate() {
  }
  componentDidMount() {
    this.loadData ();
  }
  render() {
		return (
			<Container fluid>
      <Header />
        <Nav />
        <div className="spacer50px" />
        <div className="row">
          <div className="col-md-1" />
          <div className="col-md-10">
            <div className="row">
              <div className="col-md-6 ce">
                <div className="row">
                  <div className="col-md-12" id={this.state.fade}>
                    <img src={ this.state.photo1 } onClick={this.productPhotoHandling} className="pointer" id="photo1" width="450" height="450" />
                  </div>
                </div>
                <div className="spacer50px" />
                <div className="row">
                  <div className="col-md-4 ce">
                    <img src={ this.state.photo2 } onClick={this.productPhotoHandling} className="pointer" id="photo2" width="150" height="150" />
                  </div>
                  <div className="col-md-4 ce">
                    <img src={ this.state.photo3 } onClick={this.productPhotoHandling} className="pointer" id="photo3" width="150" height="150" />
                  </div>
                  <div className="col-md-4 ce">
                    <img src={ this.state.photo4 } onClick={this.productPhotoHandling} className="pointer" id="photo4" width="150" height="150" />
                  </div>
                </div>
              </div>
              <div className="col-md-6 ce">
                <div className="row">
                  <div className="col-md-12">
                    <h2 className="capitalLetters">{this.state.productName} | {this.state.productColor} | {this.state.currency}{this.state.productPrice}</h2>
                  </div>
                </div>
                <div className="spacer50px" />
                <div className="row">
                  <div className="col-md-3">
                    <a href="#" onClick={this.productInfoHandling} className="capitalLetters smallFont blackFont" id="productDesc">Description</a>
                  </div>
                  <div className="col-md-4">
                    <a href="#" onClick={this.productInfoHandling} className="capitalLetters smallFont blackFont" id="productComp">Composition & Care</a>
                  </div>
                  <div className="col-md-2">
                    <a href="#" onClick={this.productInfoHandling} className="capitalLetters smallFont blackFont" id="productSize">Size</a>
                  </div>
                  <div className="col-md-3">
                    <a href="#" onClick={this.productInfoHandling} className="capitalLetters smallFont blackFont" id="productDeli">Delivery</a>
                  </div>
                </div>
                <div className="spacer50px" />
                <div className="row">
                  <div className="col-md-12">
                    <div className="productInfoContainer">{ this.state.productInfo }</div>
                  </div>
                </div>
                <div className="spacer50px" />
                <div className="row">
                  <div className="col-md-2" />
                  <div className="col-md-8">
                    <select id="chooseSize">
                      <option value="0">CHOOSE SIZE</option>
											<option value="1">XS</option>
											<option value="2">S</option>
											<option value="3">M</option>
                      <option value="4">L</option>
										</select>
                  </div>
                  <div className="col-md-2" />
                </div>
                <div className="spacer50px" />
                <div className="row">
                  <div className="col-md-12">
                    <div className="productInfoContainer noBorder mediumFont"><button type="button" className="cartButton">ADD TO CART</button></div>
                  </div>
                </div>
                <div className="spacer25px" />
                <div className="row">
                  <div className="col-md-12">
                    <div className="productInfoContainer noBorder mediumFont">Each item is created by our talented creative director Justyna and handmade with care in Poland.<br />If you cannot find the size, we offer CUSTOM ORDERS to all customers.<br />Email us on connect@mynalabel.com or click here for more information.</div>
                  </div>
                </div>
              </div>
            </div>
          </div>
          <div className="col-md-1" />
        </div>
        <div className="spacer50px" />
        <Footer />
          <Modal
              visible={this.state.visible}
              width="800"
              height="800"
              effect="fadeInUp"
              onClickAway={() => this.closeModal()}
            >
            <div>
              <img src={ this.state.photo1 } width="800" height="800" />
            </div>
          </Modal>
      </Container>
		);
	}
}
