import React, { useState } from 'react';
import ReactDOM from 'react-dom';
import {API_SERVER as API_SERVER} from '../src/constants';
import Cookies from 'universal-cookie';
import Header from '../components/Header';
import Nav from '../components/Nav';
import Footer from '../components/Footer';
import Ping from '../components/Ping';
import Container from 'react-bootstrap/Container';
import Button from 'react-bootstrap/Button';
import "../src/styles.css";
const cookies = new Cookies();
const session = cookies.get('session');

export default class Index extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      myEmail: '',
      loginOrEdit: 'login',
      inputEmail: '',
      loginEmail: '',
      loginToken: '',
      textOnLoginButton: 'SEND THE LOGIN LINK',
      textOnSaveButton: 'SAVE',
      firstName: '',
      lastName: '',
      birthday: '',
      dName: '',
      dAddress1: '',
      dAddress2: '',
      dCity: '',
      dState: '',
      dZip: '',
      dCountry: '',
      dComment: ''
    };

    this.handleChange = this.handleChange.bind(this);
    this.handleChange1 = this.handleChange1.bind(this);
    this.handleChange2 = this.handleChange2.bind(this);
    this.handleChange3 = this.handleChange3.bind(this);
    this.handleChange4 = this.handleChange4.bind(this);
    this.handleChange5 = this.handleChange5.bind(this);
    this.handleChange6 = this.handleChange6.bind(this);
    this.handleChange7 = this.handleChange7.bind(this);
    this.handleChange8 = this.handleChange8.bind(this);
    this.handleChange9 = this.handleChange9.bind(this);
    this.handleChange10 = this.handleChange10.bind(this);
    this.handleChange11 = this.handleChange11.bind(this);
    this.amILoggedIn = this.amILoggedIn.bind(this);
    this.sendLogin = this.sendLogin.bind(this);
    this.createToken = this.createToken.bind(this);
    this.sendMail = this.sendMail.bind(this);

  }

  handleChange (event) {
    this.setState({inputEmail: event.target.value}, () => { });
  }
  handleChange1 (event) {
    this.setState({firstName: event.target.value}, () => { });
  }
  handleChange2 (event) {
    this.setState({lastName: event.target.value}, () => { });
  }
  handleChange3 (event) {
    this.setState({birthday: event.target.value}, () => { });
  }
  handleChange4 (event) {
    this.setState({dName: event.target.value}, () => { });
  }
  handleChange5 (event) {
    this.setState({dAddress1: event.target.value}, () => { });
  }
  handleChange6 (event) {
    this.setState({dAddress2: event.target.value}, () => { });
  }
  handleChange7 (event) {
    this.setState({dCity: event.target.value}, () => { });
  }
  handleChange8 (event) {
    this.setState({dState: event.target.value}, () => { });
  }
  handleChange9 (event) {
    this.setState({dZip: event.target.value}, () => { });
  }
  handleChange10 (event) {
    this.setState({dCountry: event.target.value}, () => { });
  }
  handleChange11 (event) {
    this.setState({dComment: event.target.value}, () => { });
  }
  amILoggedIn () {
    fetch(API_SERVER + 'listen.php?part=amiloggedin&sessiontoken=' + session)
    .then(response => response.json())
		.then(output => {
      let data = output;
      let tmp = data['email'];
      if (tmp != "nodata") {
        this.setState({ myEmail: tmp, loginOrEdit: 'edit' });
      }
    })
    .catch(error => console.log(error.message));
  }
  sendLogin () {
    setTimeout(this.createToken, 100);
    setTimeout(this.sendMail, 500);
  }
  createToken () {
    fetch(API_SERVER + 'listen.php?part=loginmail&email=' + this.state.inputEmail)
    .then(response => response.json())
		.then(output => {
      let data = output;
      let tmp1 = data['email'];
      let tmp2 = data['logintoken'];
      this.setState({ loginEmail: tmp1, loginToken: tmp2 });
    })
    .catch(error => console.log(error.message));
  }
  sendMail () {
    fetch(API_SERVER + 'amazon-ses-smtp.php?part=login&email=' + this.state.loginEmail + '&token=' + this.state.loginToken, {mode: 'no-cors'});
    this.setState({ textOnLoginButton: 'SENT' });
  }

  componentDidMount() {
    this.amILoggedIn ();
  }
  render() {
    if (this.state.loginOrEdit == 'login') {
      return (
        <Container fluid>
          <Header />
          <Nav />
          <Ping />
            <div className="spacer50px" />
            <div className="row">
              <div className="col-md-12 ce capitalLetters">
                <h2><strong>Login to your account</strong></h2>
                <p>We send an auto-login link to your email address.</p>
                <div className="spacer50px" />
                <input className="loginEmail" type="text" value={this.state.inputEmail} onChange={this.handleChange} maxLength="128" placeholder="enter your email here" />
                <div className="spacer50px" />
                <div className="noBorder mediumFont"><button type="button" className="cartButton" onClick={this.sendLogin}>{this.state.textOnLoginButton}</button></div>
              </div>
            </div>
        </Container>
      );
    }
    if (this.state.loginOrEdit == 'edit') {
      return (
        <Container fluid>
          <Header />
          <Nav />
          <Ping />
            <div className="spacer10px" />
            <div className="row">
              <div className="col-md-12 ce capitalLetters">
                <h2><strong>Your account</strong></h2>
                <p>Please fill your details</p>
                <div className="spacer25px" />
                <input className="userDetails" type="text" value={this.state.firstName} onChange={this.handleChange1} maxLength="128" placeholder="enter your firstname" />
                <div className="spacer10px" />
                <input className="userDetails" type="text" value={this.state.lastName} onChange={this.handleChange2} maxLength="128" placeholder="enter your lastname" />
                <div className="spacer10px" />
                <input className="userDetails" type="text" value={this.state.birthday} onChange={this.handleChange3} maxLength="10" placeholder="your birthday (YYYY-MM-DD)" />
                <div className="spacer25px" />
                <p>Delivery / Invoice address</p>
                <input className="userDetails" type="text" value={this.state.dName} onChange={this.handleChange4} maxLength="128" placeholder="name" />
                <div className="spacer10px" />
                <input className="userDetails" type="text" value={this.state.dAddress1} onChange={this.handleChange5} maxLength="128" placeholder="address line 1" />
                <div className="spacer10px" />
                <input className="userDetails" type="text" value={this.state.dAddress2} onChange={this.handleChange6} maxLength="128" placeholder="address line 2" />
                <div className="spacer10px" />
                <input className="userDetails" type="text" value={this.state.dCity} onChange={this.handleChange7} maxLength="128" placeholder="city" />
                <div className="spacer10px" />
                <input className="userDetails" type="text" value={this.state.dState} onChange={this.handleChange8} maxLength="128" placeholder="state" />
                <div className="spacer10px" />
                <input className="userDetails" type="text" value={this.state.dZip} onChange={this.handleChange9} maxLength="64" placeholder="zip" />
                <div className="spacer10px" />
                <input className="userDetails" type="text" value={this.state.dCountry} onChange={this.handleChange10} maxLength="64" placeholder="country" />
                <div className="spacer10px" />
                <input className="userDetails" type="text" value={this.state.dComment} onChange={this.handleChange11} maxLength="256" placeholder="comment" />
                <div className="spacer10px" />
                <div className="noBorder mediumFont"><button type="button" className="cartButton" onClick={this.saveDetails}>{this.state.textOnSaveButton}</button></div>
              </div>
            </div>
        </Container>
      );
    }
	}
}
