import React, { Component } from 'react';
import {API_SERVER as API_SERVER} from '../src/constants';
import fetch from 'isomorphic-unfetch';

export default class Footer extends Component {
  constructor(props) {
    super(props);
    this.state = {
      active: '',
      value: '',
      token: '',
      placeHolder: 'EMAIL HERE'
    };
    this.handleChange = this.handleChange.bind(this);
    this.subscribePressed = this.subscribePressed.bind(this);
    this.getToken = this.getToken.bind(this);
    this.sendMail = this.sendMail.bind(this);
  }
  handleChange (event) {
    this.setState({value: event.target.value});
  }
  subscribePressed () {
    setTimeout(this.getToken, 100);
    setTimeout(this.sendMail, 500);
  }
  getToken () {
    fetch(API_SERVER + 'listen.php?part=setnewslettersubscription&email=' + this.state.value)
    .then(response => response.json())
		.then(output => {
      let data = output;
      let tmp = data['token'];
      this.setState({ token: tmp });
    })
    .catch(error => console.log(error.message));
  }
  sendMail () {
    console.log (this.state.value);
    console.log (this.state.token);
    //fetch(API_SERVER + 'amazon-ses-smtp.php?part=subscribenewsletter&email=' + this.state.value + '&token=' + this.state.token);
    this.setState({ placeHolder: 'check your mailbox', value: '' });
  }
  componentDidMount() {
  }
  render() {
    return (
      <div>
        <div className="spacer50px" />
        <hr />
        <div className="spacer25px" />
        <div className="row">
          <div className="col-md-2" />
          <div className="col-md-8">
            <div className="row">
              <div className="col-md-6">
                <div className="email_newsletter">
                  <div className="row">
                    <div className="col-md-8">
                      <input type="email" name="emailNewsletter" value={this.state.value} placeholder={this.state.placeHolder} onChange={this.handleChange} />
                    </div>
                    <div className="col-md-4 ce">
                      <button onClick={this.subscribePressed}>SUBSCRIBE</button>
                    </div>
                  </div>
                  <div className="row">
                    <div className="col-md-12 ce">
                      <div className="spacer25px" />
                      We respect your privacy.
                    </div>
                  </div>
                  <div className="row">
                    <div className="col-md-12 ce">
                      <img className="logo" src="./logo.png" alt="MYNA logo" />
                    </div>
                  </div>
                </div>
              </div>
              <div className="col-md-6">
                <div className="row">
                  <div className="col-md-4" />
                  <div className="col-md-8">
                    <p><a href="/our-story" className="blackFont">Our Story</a></p>
                    <p>Sustainability</p>
                    <p>Shipping & Returns</p>
                    <p>Contact</p>
                    <p>Privacy</p>
                  </div>
                </div>
              </div>
            </div>
          </div>
          <div className="col-md-2" />
        </div>
      </div>
    );
  }
}
